import hiive.mdptoolbox.example as example
import hiive.mdptoolbox.mdp as mdp
import gym
import time

import matplotlib.pyplot as plt

from gym.envs.toy_text.frozen_lake import generate_random_map

def set_up(size):

	if size == 'small':
		P, R = example.forest(S=100, r1=10, r2=2, p=0.1, is_sparse=False)
	else:
		P, R = example.forest(S=900, r1=10, r2=2, p=0.1, is_sparse=False)

	return P, R


def value_iter(P, R):

	results = []

	for i in range(1, 100):
		gamma = i / 100
		start_time = time.time()
		vi = mdp.ValueIteration(P, R, gamma)
		vi.run()
		duration = time.time() - start_time
		results.append({'gamma': gamma, 'policy': vi.policy, 'iterations': vi.iter, 'time': duration})

	x = [i['gamma'] for i in results]
	y_1 = [i['time'] for i in results]
	y_2 = [i['iterations'] for i in results]

	fig, ax1 = plt.subplots()

	ax1.plot(x, y_1, c="b")
	ax1.set_xlabel("Gamma")
	ax1.set_ylabel("Convergence Time (s)", c="b")

	ax2 = ax1.twinx()

	ax2.plot(x, y_2, c="r")
	ax2.set_ylabel("No. of Iterations", c="r")

	plt.title("Forest (1000 states)")
	plt.tight_layout()
	plt.savefig("vi_forest.png")
	plt.cla()


def vi_policy_compare_large(P, R):

	results = []

	gammas = [0.25, 0.5, 0.75, 1]
	for gamma in gammas:
		vi = mdp.ValueIteration(P, R, gamma)
		vi.run()
		results.append({'gamma': gamma, 'policy': vi.policy})

	f, axes = plt.subplots(2, 2) #, sharey=True)

	for index, each in enumerate(results):
		a = index % 2
		b = index // 2

		policy = each['policy']


		sq_dim = int(len(policy) ** (1 / 2))

		grid = [[0 for i in range(sq_dim)] for j in range(sq_dim)]

		for i in range(len(policy)):
			x = i % sq_dim
			y = i // sq_dim

			grid[y][x] = policy[i]
		axes[a, b].imshow(grid)
		axes[a, b].set_title(str(each['gamma']))
		axes[a, b].set_yticks([])
		axes[a, b].set_xticks([])


	plt.suptitle("Forest Management: Value Iteration \n Gamma")
	plt.savefig("figures/vi_forest_multi_large.png")
	plt.clf()



