def policy_iter(P, R):

	results = []

	for i in range(1, 100):
		gamma = i / 100
		start_time = time.time()
		pi = mdp.PolicyIteration(P, R, gamma)
		pi.run()
		duration = time.time() - start_time
		results.append({'gamma': gamma, 'policy': pi.policy, 'value': sum(pi.V)/len(pi.V), 'time': duration})

	print(results)

	x = [i['gamma'] for i in results]
	y_1 = [i['time'] for i in results]
	y_2 = [i['value'] for i in results]

	fig, ax1 = plt.subplots()

	ax1.plot(x, y_1, c="b")
	ax1.set_xlabel("Gamma")
	ax1.set_ylabel("Convergence Time (s)", c="b")

	ax2 = ax1.twinx()

	ax2.plot(x, y_2, c="r")
	ax2.set_ylabel("Value", c="r")

	plt.title("Forest: Policy Iteration")
	plt.tight_layout()
	plt.savefig("figures/pi_forest.png")
	plt.cla()

