def compare_values_large(P, R):

	results = []

	gammas = [0.1 * i for i in range(1, 11)]
	for gamma in gammas:
		q = mdp.QLearning(P, R, gamma)
		q.run()

		# pi = mdp.PolicyIteration(P, R, gamma)
		# pi.run()

		vi = mdp.ValueIteration(P, R, gamma)
		vi.run()

		results.append({'gamma': gamma, 'v_value': sum(vi.V)/len(vi.V), 'q_value': sum(q.V)/len(q.V)}) # , 'p_value': sum(pi.V)/len(pi.V)

	x = [i['gamma'] for i in results]
	y_1 = [i['v_value'] for i in results]
	y_2 = [i['q_value'] for i in results]

	plt.plot(x, y_1, label="VI Value")
	plt.plot(x, y_2, label="QL Value")

	plt.title("Forest Management")
	plt.xlabel("Gamma")
	plt.ylabel("Average Policy Value")
	plt.legend(loc="upper left")
	plt.tight_layout()
	plt.savefig("figures/forest_value_multi_large.png")
