def q_learn(P, R):

	results = []

	gammas = [0.1 * i for i in range(1, 11)]
	for gamma in gammas:
		start_time = time.time()
		q = mdp.QLearning(P, R, gamma)
		q.run()
		duration = time.time() - start_time

		results.append({'gamma': gamma, 'policy': q.policy, 'time': duration, 'value': sum(q.V)/len(q.V)})

	x = [i['gamma'] for i in results]
	y_1 = [i['time'] for i in results]
	y_2 = [i['value'] for i in results]


	fig, ax1 = plt.subplots()

	ax1.plot(x, y_1, c="b")
	ax1.set_xlabel("Gamma")
	ax1.set_ylabel("Convergence time(s)", c="b")

	ax2 = ax1.twinx()

	ax2.plot(x, y_2, c="r")
	ax2.set_ylabel("Value", c="r")


	plt.title("Forest (1000 states)")
	plt.tight_layout()
	plt.savefig("figures/ql_forest.png")
	plt.cla()


def q_learn_scan_params(P, R):

	results = []

	gammas = [0.1 * i for i in range(1,11)]
	epsilons = [0.5, 1]
	alphas = [0.5, 1]

	for gamma in gammas:
		for epsilon in epsilons:
			for alpha in alphas:
				start_time = time.time()
				q = mdp.QLearning(P, R, gamma, alpha=alpha, epsilon=epsilon)
				q.run()
				duration = time.time() - start_time

				results.append({'gamma': gamma, 'alpha': alpha, 'epsilon': epsilon, 'time': duration, 'value': sum(q.V)/len(q.V)})

	x = gammas
	y_1 = [i['value'] for i in results if i['alpha'] == 0.5 and i['epsilon'] == 0.5]
